import { useState, useEffect } from "react";
import { useSelector } from "react-redux/es/exports";

import axios from "axios";

import MatchOPPO from "./components/matchOPPO/MatchOPPO";
import AssignHitters from "./components/assignHitters/assignHitters";

import BackApi from "../api/BackApi";

const Match = () => {
  const [user, setUser] = useState(useSelector((state) => state.user.user));

  const [brief, setBrief] = useState({
    away: {
      teamName: null,
      teamLogoUrl: null,
      teamWin: null,
      teamLose: null,
      teamDraw: null,
      startingPitcher: null,
    },
    home: {
      teamName: null,
      teamLogoUrl: null,
      teamWin: null,
      teamLose: null,
      teamDraw: null,
      startingPitcher: null,
    },
  });
  const [myHitters, setMyHitters] = useState([]);

  useEffect(() => {
    if (user) {
      (async () => {
        await axios
          .get(BackApi.game.brief, {
            headers: {
              "X-ACCESS-TOKEN": user.jwt,
            },
          })
          .then((res) => {
            setBrief(res.data);
          })
          .catch((error) => {
            console.log(error);
          });
      })();

      (async () => {
        await axios
          .get(BackApi.trade.possHitters, {
            headers: {
              "X-ACCESS-TOKEN": user.jwt,
            },
          })
          .then((res) => {
            setMyHitters(res.data);
          })
          .catch((error) => {
            console.log(error);
          });
      })();
    }
  }, []);

  return (
    <>
      <MatchOPPO brief={brief}></MatchOPPO>
      <AssignHitters
        myHitters={myHitters}
        setMyHitters={setMyHitters}
      ></AssignHitters>
    </>
  );
};

export default Match;
