import { ResponsiveRadar } from "@nivo/radar";

const MyResponsiveRadar = ({ data /* see data tab */ }) => (
  <ResponsiveRadar
    data={data}
    keys={["chardonay"]}
    indexBy="taste"
    valueFormat=" >-.2f"
    margin={{ top: 70, right: 80, bottom: 40, left: 80 }}
    borderColor="#ffffff"
    gridShape="linear"
    gridLabelOffset={14}
    enableDots={false}
    dotSize={10}
    dotColor={{ theme: "background" }}
    dotBorderWidth={2}
    colors={{ scheme: "greys" }}
    fillOpacity={1}
    motionConfig="wobbly"
    legends={[
      {
        anchor: "top-left",
        direction: "column",
        translateX: -50,
        translateY: -40,
        itemWidth: 80,
        itemHeight: 20,
        itemTextColor: "#999",
        symbolSize: 12,
        symbolShape: "circle",
        effects: [
          {
            on: "hover",
            style: {
              itemTextColor: "#000",
            },
          },
        ],
      },
    ]}
  />
);

export default MyResponsiveRadar;
