import { createSlice } from "@reduxjs/toolkit/";

const initialTestState = {
  nextMatch: 0,
  matchs: [
    {
      teamSeq: 1,
      teamName: "시몬스 침대스",
      logoUrl: "https://j7d109.p.ssafy.io/download/logo01.png",
    },
    {
      teamSeq: 2,
      teamName: "농심 신라면스",
      logoUrl: "https://j7d109.p.ssafy.io/download/logo02.png",
    },
    {
      teamSeq: 3,
      teamName: "LG 이노텍스",
      logoUrl: "https://j7d109.p.ssafy.io/download/logo03.png",
    },
    {
      teamSeq: 4,
      teamName: "롯데 도리토스",
      logoUrl: "https://j7d109.p.ssafy.io/download/logo04.png",
    },
    {
      teamSeq: 5,
      teamName: "삼성 갤럭시스",
      logoUrl: "https://j7d109.p.ssafy.io/download/logo05.png",
    },
    {
      teamSeq: 6,
      teamName: "애플 아이패드스",
      logoUrl: "https://j7d109.p.ssafy.io/download/logo06.png",
    },
    {
      teamSeq: 7,
      teamName: "키움 증권스",
      logoUrl: "https://j7d109.p.ssafy.io/download/logo07.png",
    },
    {
      teamSeq: 8,
      teamName: "기아 모닝스",
      logoUrl: "https://j7d109.p.ssafy.io/download/logo08.png",
    },
    {
      teamSeq: 9,
      teamName: "카카오 게임스",
      logoUrl: "https://j7d109.p.ssafy.io/download/logo09.png",
    },
    {
      teamSeq: 10,
      teamName: "오리온 스윙칩스",
      logoUrl: "https://j7d109.p.ssafy.io/download/logo10.png",
    },
  ],
  game: {},
};

const testSlice = createSlice({
  name: "test",
  initialState: initialTestState,
  reducers: {
    setHome: (state, action) => {
      state.matchs.home = action.payload;
    },
    setNextMatch: (state, action) => {
      state.nextMatch = action.payload;
    },
  },
});

export const testActions = testSlice.actions;
export default testSlice.reducer;
