const Pitcher = ({ pitcher }) => {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemTypes.Reliefer,
    item: { reliefer: props.reliefer },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
    end: (item, monitor) => {
      const didDrop = monitor.getDropResult();
      // 구원투수를 선발투수 영역에 넣을 경우
      if (didDrop) {
        // 선발투수목록에 추가
        props.addPitchers(item.reliefer);
      }
    },
  }));

  return (
    <>
      <tr>
        <td>{pitcher.pitArm}</td>
        <td>{pitcher.name}</td>
        <td>{pitcher.era}</td>
        <td>{pitcher.game}</td>
        <td>{pitcher.inning}</td>
        <td>{pitcher.win}</td>
        <td>{pitcher.lose}</td>
      </tr>
    </>
  );
};

export default Pitcher;
