import { useState, useEffect } from "react";
import { Table } from "react-bootstrap";

import style from "../css/RelieferList.module.css";

import Reliefer from "./Reliefer";

const RelieferList = () => {
  const [reliefers, setReliefers] = useState([]);

  useEffect(() => {
    // 서버에서 구원투수 데이터 얻어와서 초기화
    setReliefers([
      {
        stand: "좌완",
        name: "리오넬 메시",
        avg: 0.314,
        onBase: 0.123,
        slug: 0.456,
        ops: 0.789,
      },
      {
        stand: "우완",
        name: "레오나르도 다빈치",
        avg: 0.314,
        onBase: 0.123,
        slug: 0.456,
        ops: 0.789,
      },
    ]);
  }, []);

  return (
    <>
      <Table className={`${style["table"]} table-borderless`}>
        <thead className={style["thead"]}>
          <tr className={style["color-cst6"]}>
            <th>스탠드</th>
            <th>이름</th>
            <th>ERA</th>
            <th>삼진</th>
            <th>실점</th>
            <th>WHIP</th>
          </tr>
        </thead>
        <tbody className={style["tbody"]}>
          {reliefers.map((reliefer, index) => (
            <Reliefer key={index} reliefer={reliefer}></Reliefer>
          ))}
        </tbody>
      </Table>
    </>
  );
};

export default RelieferList;
