      avg: 0.314,
      game: 0.123,
      atBat: 0.456,
      obp: 0.789,
      slg: 0.159,
      homerun: 2,
    },
    {
      hitterSeq: 22,
      batArm: "L",
      hitterName: "타자이름이무려열글자",
      avg: 0.314,
      game: 0.123,
      atBat: 0.456,
      obp: 0.789,
      slg: 0.159,
      homerun: 2,
    },
    {
      hitterSeq: 23,
      batArm: "L",
      hitterName: "타자23",
      avg: 0.314,
      game: 0.123,
      atBat: 0.456,
      obp: 0.789,
      slg: 0.159,
      homerun: 2,
    },
    {
      hitterSeq: 24,
      batArm: "L",
      hitterName: "타자24",
      avg: 0.314,
      game: 0.123,
      atBat: 0.456,
      obp: 0.789,
      slg: 0.159,
      homerun: 2,
    },
    {
      hitterSeq: 25,
      batArm: "L",
      hitterName: "타자25",
      avg: 0.314,
      game: 0.123,
      atBat: 0.456,
      obp: 0.789,
      slg: 0.159,
      homerun: 2,
    },
    {
      hitterSeq: 26,
      batArm: "L",
      hitterName: "타자26",
      avg: 0.314,
      game: 0.123,
      atBat: 0.456,
      obp: 0.789,
      slg: 0.159,
      homerun: 2,
    },
    {
      hitterSeq: 27,
      batArm: "L",
      hitterName: "타자27",
      avg: 0.314,
      game: 0.123,
      atBat: 0.456,
      obp: 0.789,
      slg: 0.159,
      homerun: 2,
    },
    {
      hitterSeq: 28,
      batArm: "L",
      hitterName: "타자28",
      avg: 0.314,
