import { memo } from "react";
import { Table } from "react-bootstrap";
import { useDrop } from "react-dnd";

import style from "../css/PitcherList.module.css";

import Pitcher from "./Pitcher";
import { ItemTypes } from "./ItemTypes.js";

const PitcherList = memo(() => {
  let pitchers = [
    {
      playerSeq: 1,
      pitArm: "좌완",
      playerName: "리오넬 메시",
      avg: 0.314,
      game: 0.123,
    },
    {
      layerSeq: 2,
      pitArm: "우완",
      playerName: "레오나르도 다빈치",
      avg: 0.314,
      game: 0.123,
    },
  ];

  let testid = 1;

  const [{ canDrop, isOver }, drop] = useDrop(() => ({
    accept: ItemTypes.Reliefer,
    drop: () => ({ name: "PitcherList" }),
    collect: (monitor) => ({
      isOver: monitor.isOver(),
      canDrop: monitor.canDrop(),
    }),
  }));
  const isActive = canDrop && isOver;
  let backgroundColor = "#ffffff00";
  if (isActive) {
    backgroundColor = "darkgreen";
  } else if (canDrop) {
    backgroundColor = "darkkhaki";
  }

  return (
    <>
      <Table className={`${style["table"]} table-borderless`}>
        <thead className={style["thead"]}>
          <tr className={style["color-cst6"]}>
            <th>스탠드</th>
            <th>이름</th>
            <th>ERA</th>
            <th>게임수</th>
          </tr>
        </thead>
        <tbody
          className={style["tbody"]}
          ref={drop}
          style={{ backgroundColor }}
          data-testid="pitcherList"
        >
          {pitchers.map((pitcher) => (
            <Pitcher key={testid++} pitcher={pitcher}></Pitcher>
          ))}
        </tbody>
      </Table>
    </>
  );
});

export default PitcherList;
