import MatchOPPO from "./components/matchOPPO/MatchOPPO";
import AssignHitters from "./components/assignHitters/AssignHitters";
import { Button } from "react-bootstrap";
import { useState } from "react";

const Match = () => {
  const [test] = useState([true, false]);

  return (
    <>
      <Button
        onClick={() => {
          alert("hi?");
          test[0] = !test[0];
          test[1] = !test[1];
        }}
      >
        hi
      </Button>
      {test[0] && <MatchOPPO></MatchOPPO>}
      {test[1] && <AssignHitters></AssignHitters>}
    </>
  );
};

export default Match;
