import { Col, Row } from "react-bootstrap";
import MyHitterList from "./MyHitterList";
import Hitter from "./Hitter";
import Pitcher from "./Pitcher";

import { AiFillPlayCircle } from "react-icons/ai";

import style from "../../css/assignHitters/AssignHitters.module.css";
import { useState } from "react";

const AssignHitters = () => {
  const [myHitters, setMyHitters] = useState([
    {
      hitterSeq: 19,
      batArm: "우타",
      name: "타자19",
      avg: 0.314,
      game: 0.123,
      atBat: 0.456,
      obp: 0.789,
      slg: 0.159,
      homerun: 2,
    },
    {
      hitterSeq: 20,
      batArm: "좌타",
      name: "타자20",
      avg: 0.314,
      game: 0.123,
      atBat: 0.456,
      obp: 0.789,
      slg: 0.159,
      homerun: 2,
    },
    {
      hitterSeq: 21,
      batArm: "우타",
      name: "타자21",
      avg: 0.314,
      game: 0.123,
      atBat: 0.456,
      obp: 0.789,
      slg: 0.159,
      homerun: 2,
    },
    {
      hitterSeq: 22,
      batArm: "좌타",
      name: "타자22",
      avg: 0.314,
      game: 0.123,
      atBat: 0.456,
      obp: 0.789,
      slg: 0.159,
      homerun: 2,
    },
  ]);
  const [hitters, setHitters] = useState([
    { hitterSeq: 1, hitterName: "박찬호", hitArm: "L" },
    null,
    null,
    null,
    null,
    null,
    null,
    null,
    null,
  ]);

  const pitcher = { pitcherSeq: 1, pitcherName: "박찬호", pitArm: "L" };

  return (
    <>
      <Row className={style["background"]}>
        <div style={{ marginTop: "auto", marginBottom: "auto" }}>
          <Row className={style["mainHead"]}>
            <div>타선 지정</div>
          </Row>
          <Row className={style["mainBody"]}>
            <Col>
              <Row className={style["playerHead"]}>
                <div>보유 선수</div>
              </Row>
              <Row className={style["playerBody"]}>
                <MyHitterList hitters={myHitters} />
              </Row>
            </Col>
            <Col>
              <Row>
                <Row className={style["oppoPitcherHead"]}>
                  <div>상대의 선발 투수</div>
                </Row>
                <Row className={style["oppoPitcherBody"]}>
                  <div>
                    <div
                      className={style["stand"]}
                      style={{ backgroundColor: true ? "#bf0d3e" : "#0d5fbf" }}
                    >
                      좌완
                    </div>
                    류현진
                  </div>
                </Row>
              </Row>
              <Row>
                <Row className={style["hitterHead"]}>
                  <div>선발 타자</div>
                </Row>
                <Row className={style["hitterBody"]}>
                  {hitters.map((hitter, index) => {
                    return (
                      <Hitter
                        hitter={hitter}
                        key={index}
                        idx={index + 1}
                      ></Hitter>
                    );
                  })}
                  {<Pitcher pitcher={pitcher} />}
                </Row>
              </Row>
              <Row
                className={style["btn"]}
                onClick={() => {
                  alert(hitters.length);
                }}
              >
                <div>
                  NEXT <AiFillPlayCircle className={style["start"]} />
                </div>
              </Row>
            </Col>
          </Row>
        </div>
      </Row>
    </>
  );
};

export default AssignHitters;
