import { Row } from "react-bootstrap";
import { useDrop } from "react-dnd";
import { ItemTypes } from "./ItemTypes.js";

import style from "../../css/assignHitters/Player.module.css";

const Hitter = (props) => {
  const [{ canDrop, isOver }, drop] = useDrop(() => ({
    accept: ItemTypes.MyHitter,
    drop: () => ({ name: "hitterList" }),
    collect: (monitor) => ({
      isOver: monitor.isOver(),
      canDrop: monitor.canDrop(),
    }),
  }));
  const isActive = canDrop && isOver;
  let backgroundColor = "#dfdfdf";
  if (isActive) {
    backgroundColor = "#aaaaaa6b";
  } else if (canDrop) {
    backgroundColor = "#aaaaaa32";
  }

  return (
    <>
      <Row style={{ margin: "0", padding: "0" }}>
        <div className={style["seq"]}>{props.idx}</div>
        <div
          className={style["playerInfo"]}
          ref={drop}
          style={{ backgroundColor }}
        >
          {props.hitter ? (
            props.hitter.hitArm === "L" ? (
              <div className={style["leftHitter"]}></div>
            ) : (
              <div className={style["rightHitter"]}></div>
            )
          ) : null}
          {props.hitter ? props.hitter.hitterName : null}
        </div>
      </Row>
    </>
  );
};

export default Hitter;
