import { useState, useEffect } from "react";
import { Table } from "react-bootstrap";

import style from "../css/PitcherList.module.css";

import Pitcher from "./Pitcher";

const PitcherList = () => {
  const [pitchers, setPitchers] = useState([]);

  useEffect(() => {
    // 서버에서 선발투수 데이터 얻어와서 초기화
    setPitchers([
      {
        playerSeq: 1,
        pitArm: "좌완",
        playerName: "리오넬 메시",
        avg: 0.314,
        game: 0.123,
      },
      {
        layerSeq: 2,
        pitArm: "우완",
        playerName: "레오나르도 다빈치",
        avg: 0.314,
        game: 0.123,
      },
    ]);
  }, []);

  return (
    <>
      <Table className={`${style["table"]} table-borderless`}>
        <thead className={style["thead"]}>
          <tr className={style["color-cst6"]}>
            <th>스탠드</th>
            <th>이름</th>
            <th>ERA</th>
            <th>게임수</th>
          </tr>
        </thead>
        <tbody className={style["tbody"]}>
          {pitchers.map((pitcher, index) => (
            <Pitcher key={index} pitcher={pitcher}></Pitcher>
          ))}
        </tbody>
      </Table>
    </>
  );
};

export default PitcherList;
