import { useState } from "react";

import style from "../css/test.module.css";

const Hitter = ({ hitter }) => {
  const [likeList, setLikeList] = useState("");

  const testobj = () => {
    return <div className="likes__list"></div>;
  };
  const handleLeave = () => {
    setLikeList("");
  };
  const handleHover = () => {
    setLikeList(testobj);
    const circle = document.querySelector(".circle");
  };

  return (
    <>
      <tr onMouseOver={handleHover} onMouseLeave={handleLeave}>
        <td>{hitter.batArm}</td>
        <td>{hitter.playerName}</td>
        <td>{hitter.avg}</td>
        <td>{hitter.game}</td>
        <td>{hitter.atBat}</td>
        <td>{hitter.obp}</td>
        <td>{hitter.slg}</td>
        <td>{hitter.homerun}</td>
      </tr>
      {likeList}
    </>
  );
};
export default Hitter;
