import { ResponsiveRadar } from "@nivo/radar";

const MyResponsiveRadar = ({ data }) => (
  <>
    <ResponsiveRadar
      data={data}
      // keys={["chardonay", "carmenere", "syrah"]}
      indexBy="taste"
      valueFormat=" >-.2f"
      margin={{ top: 70, right: 80, bottom: 40, left: 80 }}
      borderColor={{ from: "color", modifiers: [] }}
      gridShape="linear"
      gridLabelOffset={13}
      enableDots={false}
      dotSize={10}
      dotColor="#000000"
      dotBorderWidth={2}
      dotBorderColor="black"
      colors={{ scheme: "nivo" }}
      fillOpacity={1}
      blendMode="multiply"
      motionConfig="wobbly"
      legends={[
        {
          anchor: "top-left",
          direction: "column",
          translateX: -50,
          translateY: -40,
          itemWidth: 80,
          itemHeight: 20,
          itemTextColor: "#999",
          symbolSize: 12,
          symbolShape: "circle",
          effects: [
            {
              on: "hover",
              style: {
                itemTextColor: "#000",
              },
            },
          ],
        },
      ]}
    />
  </>
);

export default MyResponsiveRadar;
