import { createContext, MouseEvent } from 'react'
import { TooltipAnchor } from './types'

export interface TooltipActionsContextData {
    showTooltipAt: (
        content: JSX.Element,
        position: [number, number],
        anchor?: TooltipAnchor
    ) => void
    showTooltipFromEvent: (content: JSX.Element, event: MouseEvent, anchor?: TooltipAnchor) => void
    hideTooltip: () => void
}

const defaultActions: TooltipActionsContextData = {
    showTooltipAt: () => {},
    showTooltipFromEvent: () => {},
    hideTooltip: () => {},
}

export const TooltipActionsContext = createContext<TooltipActionsContextData>(defaultActions)

export interface TooltipStateContextDataVisible {
    isVisible: true
    position: [number, number]
    content: JSX.Element
    anchor: TooltipAnchor
}

export interface TooltipStateContextDataHidden {
    isVisible: false
    position: [null, null]
    content: null
    anchor: null
}

export type TooltipStateContextData = TooltipStateContextDataVisible | TooltipStateContextDataHidden

export const hiddenTooltipState: TooltipStateContextDataHidden = {
    isVisible: false,
    position: [null, null],
    content: null,
    anchor: null,
}

export const TooltipStateContext = createContext<TooltipStateContextData>(hiddenTooltipState)
import { useState, useContext, useCallback, MutableRefObject, MouseEvent, useMemo } from 'react'
import {
    TooltipActionsContext,
    TooltipActionsContextData,
    TooltipStateContext,
    TooltipStateContextData,
    hiddenTooltipState,
} from './context'
import { TooltipAnchor } from './types'

export const useTooltipHandlers = (container: MutableRefObject<HTMLDivElement>) => {
    const [state, setState] = useState<TooltipStateContextData>(hiddenTooltipState)

    const showTooltipAt: TooltipActionsContextData['showTooltipAt'] = useCallback(
        (content: JSX.Element, [x, y]: [number, number], anchor: TooltipAnchor = 'top') => {
            setState({
                isVisible: true,
                position: [x, y],
                anchor,
                content,
            })
        },
        [setState]
    )

    const showTooltipFromEvent: TooltipActionsContextData['showTooltipFromEvent'] = useCallback(
        (content: JSX.Element, event: MouseEvent, anchor: TooltipAnchor = 'top') => {
            const bounds = container.current.getBoundingClientRect()
            const offsetWidth = container.current.offsetWidth
            // In a normal situation mouse enter / mouse leave events
            // capture the position ok. But when the chart is inside a scaled
            // element with a CSS transform like: `transform: scale(2);`
            // tooltip are not positioned ok.
            // Comparing original width `offsetWidth` agains scaled
            // width give us the scaling factor to calculate
            // ok mouse position
            const scaling = offsetWidth === bounds.width ? 1 : offsetWidth / bounds.width
            const x = (event.clientX - bounds.left) * scaling
            const y = (event.clientY - bounds.top) * scaling

            if (anchor === 'left' || anchor === 'right') {
                if (x < bounds.width / 2) anchor = 'right'
                else anchor = 'left'
            }

            setState({
                isVisible: true,
                position: [x, y],
                anchor,
                content,
            })
        },
        [container, setState]
    )

    const hideTooltip = useCallback(() => {
        setState(hiddenTooltipState)
    }, [setState])

    const actions: TooltipActionsContextData = useMemo(() => {
        return {
            showTooltipAt,
            showTooltipFromEvent,
            hideTooltip,
        }
    }, [showTooltipAt, showTooltipFromEvent, hideTooltip])

    return {
        actions,
        state,
    }
}

export const useTooltip = () => {
    const context = useContext(TooltipActionsContext)
    if (context === undefined) {
        throw new Error('useTooltip must be used within a TooltipProvider')
    }

    return context
}

export const useTooltipState = () => {
    const context = useContext(TooltipStateContext)
    if (context === undefined) {
        throw new Error('useTooltipState must be used within a TooltipProvider')
    }

    return context
}
import { useTooltipState } from './hooks'
import { TooltipWrapper } from './TooltipWrapper'
import { TooltipStateContextData, TooltipStateContextDataVisible } from './context'

export const isVisibleTooltipState = (
    state: TooltipStateContextData
): state is TooltipStateContextDataVisible => state.isVisible

export const Tooltip = () => {
    const state = useTooltipState()

    if (!isVisibleTooltipState(state)) {
        return null
    }

    return (
        <TooltipWrapper position={state.position} anchor={state.anchor}>
            {state.content}
        </TooltipWrapper>
    )
}
import { PropsWithChildren, MutableRefObject } from 'react'
import { TooltipActionsContext, TooltipStateContext } from './context'
import { useTooltipHandlers } from './hooks'

interface TooltipProviderProps {
    container: MutableRefObject<HTMLDivElement>
}

export const TooltipProvider = ({
    container,
    children,
}: PropsWithChildren<TooltipProviderProps>) => {
    const { actions, state } = useTooltipHandlers(container)

    return (
        <TooltipActionsContext.Provider value={actions}>
            <TooltipStateContext.Provider value={state}>{children}</TooltipStateContext.Provider>
        </TooltipActionsContext.Provider>
    )
}
