import { useState } from "react";

import style from "../css/test.module.css";

const Hitter = ({ hitter }) => {
  const [likeList, setLikeList] = useState("");

  const testobj = () => {
    const testStyle = {
      position: "absolute",
      top: "0",
      left: "0",
      width: "150px",
      height: "150px",
      "border-radius": "50%",
      "background-color": "#ee6c4d",
      transform: "translate(-50%, -50%)",
    };

    return <div className="circle" style={testStyle}></div>;
  };
  const handleLeave = () => {
    setLikeList("");
  };
  const handleHover = () => {
    setLikeList(testobj);
    const circle = document.querySelector(".circle");
  };

  return (
    <>
      <tr onMouseOver={handleHover} onMouseLeave={handleLeave}>
        <td>{hitter.batArm}</td>
        <td>{hitter.playerName}</td>
        <td>{hitter.avg}</td>
        <td>{hitter.game}</td>
        <td>{hitter.atBat}</td>
        <td>{hitter.obp}</td>
        <td>{hitter.slg}</td>
        <td>{hitter.homerun}</td>
      </tr>
      {likeList}
    </>
  );
};
export default Hitter;
