import { useState, useEffect, useRef, useReducer } from "react";
import { Col, Container, Row } from "react-bootstrap";
import { Link } from "react-router-dom";
import { DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";

import style from "./css/Manage.module.css";

import HitterList from "./components/HitterList";
import PitcherList from "./components/PitcherList";
import RelieferList from "./components/RelieferList";

const reducer = (state, action) => {
  switch (action.type) {
    case "addPitcher":
      return [...state, ...action.val];
    case "removeReliefer":
      alert("why?");
      return state.filter((item) => {
        return item.pitcherSeq !== action.val;
      });
    default:
      return state;
  }
};

const Manage = () => {
  const initHittersRef = useRef([]);
  const initPitchersRef = useRef([]);
  const initReliefersRef = useRef([]);

  const [hitters, setHitters] = useState([]);
  const [pitchers, setPitchers] = useState([]);
  const [reliefers, setReliefers] = useState([]);

  const [pitchersR, setPitchersR] = useReducer(reducer, []);
  const [reliefersR, setReliefersR] = useReducer(reducer, []);

  // 컴포넌트가 마운트 될때
  useEffect(() => {
    // 서버에서 데이터 얻어와서 초기화 (hitters, pitchers, reliefers, initPitchers, initReliefers)
    initHittersRef.current = [
      {
        hitterSeq: 1,
        batArm: "우타",
        name: "리오넬 메시",
        avg: 0.314,
        game: 0.123,
        atBat: 0.456,
        obp: 0.789,
        slg: 0.159,
        homerun: 2,
      },
      {
        hitterSeq: 2,
        batArm: "좌타",
        name: "레오나르도 다빈치",
        avg: 0.314,
        game: 0.123,
        atBat: 0.456,
        obp: 0.789,
        slg: 0.159,
        homerun: 2,
      },
    ];
    initPitchersRef.current = [
      {
        pitcherSeq: 1,
        pitArm: "좌완",
        name: "선발투수1",
        era: 0.274,
        game: 50,
        inning: 180,
        win: 37,
        lose: 11,
      },
      {
        pitcherSeq: 2,
        pitArm: "우완",
        name: "선발투수2",
        era: 0.274,
        game: 50,
        inning: 180,
        win: 37,
        lose: 11,
      },
    ];
    initReliefersRef.current = [
      {
        pitcherSeq: 1,
        pitArm: "좌완",
        name: "구원투수1",
        era: 0.274,
        game: 50,
        inning: 180,
        win: 37,
        lose: 11,
      },
      {
        pitcherSeq: 2,
        pitArm: "우완",
        name: "구원투수2",
        era: 0.274,
        game: 50,
        inning: 180,
        win: 37,
        lose: 11,
      },
      {
        pitcherSeq: 3,
        pitArm: "우완",
        name: "구원투수3",
        era: 0.274,
        game: 50,
        inning: 180,
        win: 37,
        lose: 11,
      },
    ];

    setPitchersR({ type: "addPitcher", val: initPitchersRef.current });
    setReliefersR(initReliefersRef.current);
    setHitters(initHittersRef.current);
    setPitchers(initPitchersRef.current);
    setReliefers(initReliefersRef.current);
  }, []);

  // reset 버튼 함수
  const reset = () => {
    // pitchers, reliefers를 save 하기전 값들인 initPitchersin, itReliefers으로 재초기화
    setPitchers(initPitchersRef.current);
    setReliefers(initReliefersRef.current);
  };

  // save 버튼 함수
  const save = () => {
    // 현재 pitchers, reliefers 의 목록을 서버에 전달
    // 구단관리 API에 저장 관련 API필요 => 백엔드에 요청
    // console.log(pitchers);
    // console.log(reliefers);
    console.log(pitchersR);
  };

  //
  const addPitchers = (reliefer) => {
    setPitchersR({ type: "addPitcher", val: [reliefer] });
    setReliefersR({ type: "removeReliefer", val: reliefer.pitcherSeq });
    // setPitchers((pitchers) => {
    //   return [...pitchers, reliefer];
    // });
    // setReliefers((reliefers) => {
    //   return reliefers.filter((item) => {
    //     return item.pitcherSeq !== reliefer.pitcherSeq;
    //   });
    // });
  };

  // // 컴포넌트가 업데이트 될때마다
  // useEffect(() => {
  //   console.log(pitchers);
  //   console.log(reliefers);
  // });

  return (
    <>
      <DndProvider backend={HTML5Backend}>
        <Container
          fluid
          className={`${style["background"]} ${style["manage"]}`}
        >
          <Container className={style["clubHead"]}>구단 관리</Container>
          <Container className={style["clubBody"]}>
            <Row className={style["clubBody-row1"]}>
              <Col className={style["clubBody-row1-col1"]}>
                <Row className={style["clubBody-row1-col1-row"]}>
                  <Container className={style["batterHead"]}>
                    보유 타자
                  </Container>
                  <Container className={style["batterBody"]}>
                    <HitterList hitters={hitters}></HitterList>
                  </Container>
                </Row>
              </Col>
              <Col className={style["clubBody-row1-col2"]}>
                <Row className={style["clubBody-row1-col2-row1"]}>
                  <Container className={style["pircherHead"]}>
                    선발 로테이션
                  </Container>
                  <Container className={style["pircherBody"]}>
                    <PitcherList pitchers={pitchersR}></PitcherList>
                  </Container>
                </Row>
                <Row className={style["clubBody-row1-col2-row2"]}>
                  <Container className={style["pircherHead"]}>
                    구원 투수
                  </Container>
                  <Container className={style["pircherBody"]}>
                    <RelieferList
                      reliefers={reliefers}
                      addPitchers={addPitchers}
                    ></RelieferList>
                  </Container>
                </Row>
              </Col>
            </Row>
            <Row className={style["clubBody-row2"]}>
              <div className={style["clubBody-row2-div"]}>
                <div
                  className={`${style["clubBody-row2-div-btn"]} ${style["bg-color-cst1"]}`}
                >
                  <Link className={style["link"]} to="/main">
                    MAIN
                  </Link>
                </div>
                &nbsp;&nbsp;
                <div
                  className={`${style["clubBody-row2-div-btn"]} ${style["bg-color-cst3"]}`}
                >
                  <Link className={style["link"]} to="/market">
                    이적시장
                  </Link>
                </div>
                &nbsp;&nbsp;
                <div
                  className={`${style["clubBody-row2-div-btn"]} ${style["bg-color-cst4"]}`}
                  onClick={reset}
                >
                  RESET
                </div>
                &nbsp;&nbsp;
                <div
                  className={`${style["clubBody-row2-div-btn"]} ${style["bg-color-cst5"]}`}
                  onClick={save}
                >
                  SAVE
                </div>
              </div>
            </Row>
          </Container>
        </Container>
      </DndProvider>
    </>
  );
};

export default Manage;
