import { memo } from "react";
import { Table } from "react-bootstrap";
import { useDrop } from "react-dnd";

import style from "../css/RelieferList.module.css";

import Reliefer from "./Bullpen";
import { ItemTypes } from "./ItemTypes.js";

const BullpenList = memo((props) => {
  const [{ canDrop, isOver }, drop] = useDrop(() => ({
    accept: ItemTypes.Pitcher,
    drop: () => ({ name: "RelieferList" }),
    collect: (monitor) => ({
      isOver: monitor.isOver(),
      canDrop: monitor.canDrop(),
    }),
  }));
  const isActive = canDrop && isOver;
  let backgroundColor = "#ffffff00";
  if (isActive) {
    backgroundColor = "#aaaaaa6b";
  } else if (canDrop) {
    backgroundColor = "#aaaaaa32";
  }

  return (
    <>
      <Table className={`${style["table"]} table-borderless`}>
        <thead className={style["thead"]}>
          <tr className={style["color-cst6"]}>
            <th>스탠드</th>
            <th>이름</th>
            <th>ERA</th>
            <th>게임수</th>
            <th>이닝수</th>
            <th>승리</th>
            <th>패배</th>
          </tr>
        </thead>
        <tbody
          className={style["tbody"]}
          ref={drop}
          style={{ backgroundColor }}
          data-testid="relieferList"
        >
          {props.reliefers.map((reliefer, index) => (
            <Reliefer
              key={index}
              reliefer={reliefer}
              addPitchers={props.addPitchers}
            ></Reliefer>
          ))}
        </tbody>
      </Table>
    </>
  );
});

export default BullpenList;
