import { useState, useEffect } from "react";
import { Row, Col } from "react-bootstrap";
import { useNavigate } from "react-router";
import { BsPencilSquare } from "react-icons/bs";
import { useSelector } from "react-redux";

import axios from "axios";

import UserInfoModal from "./components/UserInfoModal";
import BackDrop from "./components/BackDrop";
import MatchList from "./components/MatchList";
import PitcherList from "./components/PitcherList";
import title from "../assets/etc/title.png";
import subtitle from "../assets/etc/subtitle.png";
import teamLogo from "../assets/etc/ground2.png";

import style from "./css/Main.module.css";

import BackApi from "../api/BackApi";

const Main = () => {
  const user = useSelector((state) => state.user.user);

  const navigate = useNavigate();

  const [menuBar, setMeunuBar] = useState([true, false, false, false]);
  const [matchs, setMatchs] = useState([]);
  const [pitchers, setPitchers] = useState([]);
  const [schedules, setSchedules] = useState([
    {
      teamSeq: null,
      teamName: null,
      logoUrl: null,
    },
    {
      teamSeq: null,
      teamName: null,
      logoUrl: null,
    },
    {
      teamSeq: null,
      teamName: null,
      logoUrl: null,
    },
  ]);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [jwt_token, setJwtToken] = useState();

  const activeMenu = "rgba(5, 20, 48, 0.5)";

  useEffect(() => {
    setJwtToken(user.jwt);
  }, [user]);

  useEffect(() => {
    // 선발 로테이션 조회
    (async () => {
      await axios
        .get(BackApi.manage.rotation, {
          headers: {
            "X-ACCESS-TOKEN": jwt_token,
          },
        })
        .then((res) => {
          let pits = [];
          const keys = Object.keys(res.data);
          for (let i = 0; i < keys.length; i++) {
            const key = keys[i];
            const value = res.data[key];
            pits.push(value);
          }
          setPitchers(pits);
        })
        .catch((error) => {
          console.log(error);
        });
    })();
    // 경기 일정 조회
    (async () => {
