import { useEffect, memo } from "react";
import { Table } from "react-bootstrap";

import style from "../css/HitterList.module.css";

import Hitter from "./Hitter";

const HitterList = memo((props) => {
  const hitters = props.hitters;
  const setHitters = props.setHitters;

  useEffect(() => {
    // 서버에서 타자 데이터 얻어와서 초기화
    setHitters([
      {
        playerSeq: 1,
        batArm: "우타",
        playerName: "리오넬 메시",
        avg: 0.314,
        game: 0.123,
        atBat: 0.456,
        obp: 0.789,
        slg: 0.159,
        homerun: 2,
      },
      {
        playerSeq: 2,
        batArm: "좌타",
        playerName: "레오나르도 다빈치",
        avg: 0.314,
        game: 0.123,
        atBat: 0.456,
        obp: 0.789,
        slg: 0.159,
        homerun: 2,
      },
    ]);
  }, [setHitters]);

  return (
    <>
      <Table className={`${style["table"]} table-borderless`}>
        <thead className={style["thead"]}>
          <tr className={style["color-cst6"]}>
            <th>스탠드</th>
            <th>이름</th>
            <th>타율</th>
            <th>게임수</th>
            <th>타수</th>
            <th>OBP</th>
            <th>SLG</th>
            <th>홈런</th>
          </tr>
        </thead>
        <tbody className={style["tbody"]}>
          {hitters.map((hitter, index) => (
            <Hitter key={index} hitter={hitter}></Hitter>
          ))}
        </tbody>
      </Table>
    </>
  );
});

export default HitterList;
