import { useState } from "react";

import HitterDetailModal from "./HitterDetailModal";

const Hitter = ({ hitter }) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const onMouseOver = () => {
    setModalIsOpen(true);
  };

  const onMouseLeave = () => {
    setModalIsOpen(false);
  };

  return (
    <>
      <tr onMouseOver={onMouseOver} onMouseLeave={onMouseLeave}>
        <td>{hitter.batArm}</td>
        <td>{hitter.name}</td>
        <td>{hitter.avg}</td>
        <td>{hitter.game}</td>
        <td>{hitter.atBat}</td>
        <td>{hitter.obp}</td>
        <td>{hitter.slg}</td>
        <td>{hitter.homerun}</td>
      </tr>
      {modalIsOpen && <HitterDetailModal />}
    </>
  );
};
export default Hitter;
